<?phprequire_once('dbconfig.php');class categorias{	private $conn;	public function __construct(){		$database = new Database();		$db = $database->dbConnection();		$this->conn = $db;    }	public function runQuery($sql){		$stmt = $this->conn->prepare($sql);		return $stmt;	}	public function register($valor){		try{		    $nome = $valor["nome"];            $categoriaid= $valor["categoriaid"];            if($nome=="")	{                return "preencha o nome!";            }            else {                if ($categoriaid <= 0) {                    $stmt = $this->conn->prepare("INSERT INTO categorias(nome) VALUES(:nome)");                    $stmt->bindparam(":nome", $nome);                } else {                    $stmt = $this->conn->prepare("UPDATE categorias SET nome = :nome WHERE categoriaid = :categoriaid");                    $stmt->bindparam(":nome", $nome);                    $stmt->bindparam(":categoriaid", $categoriaid);                }                $stmt->execute();                return "0";            }		}		catch(PDOException $e){			echo $e->getMessage();		}	}    public function deletar($uid){        try{            $stmt = $this->conn->prepare("UPDATE categorias SET excluido = 'S' WHERE categoriaid = :uid");            $stmt->bindparam(":uid", $uid);            $stmt->execute();            return $stmt;        }        catch(PDOException $e) {            echo $e->getMessage();        }    }	public function redirect($url){		header("Location: $url");	}}?>