<?phprequire_once('dbconfig.php');class produtos{	private $conn;	public function __construct(){		$database = new Database();		$db = $database->dbConnection();		$this->conn = $db;    }	public function runQuery($sql){		$stmt = $this->conn->prepare($sql);		return $stmt;	}	public function register($valor){		try{		    $nome = $valor["nome"];            $produtoid = $valor["produtoid"];            $categoriaid = $valor["categoriaid"];            if($nome=="")	{                return "preencha o nome!";            }            else {                if ($produtoid <= 0) {                    $stmt = $this->conn->prepare("INSERT INTO produtos(nome,categoriaid) VALUES(:nome, :categoriaid)");                    $stmt->bindparam(":nome", $nome);                    $stmt->bindparam(":categoriaid", $categoriaid);                    $stmt->execute();                } else {                    $stmt = $this->conn->prepare("UPDATE produtos SET nome = :nome, categoriaid = :categoriaid WHERE produtoid = :produtoid");                    $stmt->bindparam(":nome", $nome);                    $stmt->bindparam(":categoriaid", $categoriaid);                    $stmt->bindparam(":produtoid", $produtoid);                    $stmt->execute();                }                return "0";            }		}		catch(PDOException $e){			echo $e->getMessage();		}	}    public function deletar($uid){        try{            $stmt = $this->conn->prepare("UPDATE produtos SET excluido = 'S' WHERE produtoid = :uid");            $stmt->bindparam(":uid", $uid);            $stmt->execute();            return $stmt;        }        catch(PDOException $e) {            echo $e->getMessage();        }    }	public function redirect($url){		header("Location: $url");	}}?>